func PrintRect(buffer []byte, pitch int) {
	toPrint := ""
	for i, c := range buffer {
		if i%pitch == 0 {
			toPrint += string('\n')
		}
		toPrint += string(c)
	}
	fmt.Println(toPrint)
}

func CopyRect(BufferA, BufferB []byte, PitchA, PitchB, FromMinX, FromMinY, FromMaxX, FromMaxY, ToMinX, ToMinY int) {
	bx, by := ToMinX, ToMinY

	for y := FromMinY; y < FromMaxY; y++ {
		for x := FromMinX; x < FromMaxX; x++ {
			BufferB[bx+by*PitchB] = BufferA[x+y*PitchA]
			bx++
		}
		by++
		bx = ToMinX
	}

	fmt.Println("done")
}
